package com.junang.openview.delegation;import android.support.design.widget.AppBarLayout;import android.support.design.widget.CoordinatorLayout;import android.support.v4.view.AsyncLayoutInflater;import android.support.v4.widget.NestedScrollView;import android.view.LayoutInflater;/** * Created by junius.ang on 8/25/2016. */public class CoreDelegateDependency {    protected LayoutInflater mLayoutInflater;    protected CoordinatorLayout mCoordinatorLayout;    protected AppBarLayout mAppBarLayout;    protected NestedScrollView mContentScrollLayout;    protected AsyncLayoutInflater mAsyncLayoutInflater;    public CoreDelegateDependency(AsyncLayoutInflater asyncLayoutInflater, LayoutInflater layoutInflater, CoordinatorLayout coordinatorLayout, AppBarLayout appBarLayout, NestedScrollView frameLayout) {        this.mAsyncLayoutInflater = asyncLayoutInflater;        this.mLayoutInflater = layoutInflater;        this.mCoordinatorLayout = coordinatorLayout;        this.mAppBarLayout = appBarLayout;        this.mContentScrollLayout = frameLayout;    }    public AppBarLayout getAppBarLayout() {        return mAppBarLayout;    }    public void setAppBarLayout(AppBarLayout mAppBarLayout) {        this.mAppBarLayout = mAppBarLayout;    }    public CoordinatorLayout getCoordinatorLayout() {        return mCoordinatorLayout;    }    public void setCoordinatorLayout(CoordinatorLayout mCoordinatorLayout) {        this.mCoordinatorLayout = mCoordinatorLayout;    }    public LayoutInflater getLayoutInflater() {        return mLayoutInflater;    }    public void setLayoutInflater(LayoutInflater mLayoutInflater) {        this.mLayoutInflater = mLayoutInflater;    }    public AsyncLayoutInflater getAsyncLayoutInflater() {        return mAsyncLayoutInflater;    }    public void setAsyncLayoutInflater(AsyncLayoutInflater mAsyncLayoutInflater) {        this.mAsyncLayoutInflater = mAsyncLayoutInflater;    }}